class Solution {
public:
    int minFlips(vector<vector<int>>& grid)
    {
        int n=grid.size();
        int m=grid[0].size();
        map<pair<int,int>,int> mp4;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                vector<int> v={grid[i][j],grid[i][m-1-j],grid[n-1-i][j],grid[n-1-i][m-1-j]};
                vector<int> cnt(2);
                for(auto x:v)
                    cnt[x]++;
                mp4[{cnt[0],cnt[1]}]++;
            }
        }
        map<pair<int,int>,int> mp2;
        if(n%2)
        {
            int i=n/2;
            for(int j=0;j<m/2;j++)
            {
                vector<int> v={grid[i][j],grid[i][m-1-j]};
                vector<int> cnt(2);
                for(auto x:v)
                    cnt[x]++;
                mp2[{cnt[0],cnt[1]}]++;
            }
        }
        if(m%2)
        {
            int j=m/2;
            for(int i=0;i<n/2;i++)
            {
                vector<int> v={grid[i][j],grid[n-1-i][j]};
                vector<int> cnt(2);
                for(auto x:v)
                    cnt[x]++;
                mp2[{cnt[0],cnt[1]}]++;
            }
        }
        int tag=0;
        if(n%2 && m%2)
            tag=grid[n/2][m/2];

        int ans=0;
        for(auto [k,v]:mp4)
        {
            auto [c0,c1]=k;
            ans+=min(c0,c1)*v;
        }
        if(mp2[{0,2}]%2==1)
        {
            if(mp2[{1,1}])
            {
                ans++;
                mp2[{1,1}]--;
                mp2[{0,2}]++;
            }
            else
            {
                ans+=2;
                mp2[{0,2}]--;
                mp2[{2,0}]++;
            }
        }
        for(auto [k,v]:mp2)
        {
            auto [c0,c1]=k;
            if(c0 && c1)
                ans+=min(c0,c1)*v;
        }
        if(tag)
            ans++;
        return ans;
    }
};
