class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0,a1=0,diff=0;
        for(int i=0;i<n;i++){
            if(i > n-1-i)break;
            for(int j=0;j<m;j++){
                if(j > m-1-j)break;
                int a = grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j];
                if(i==n-1-i && j==m-1-j){
                    if(grid[i][j]==1)ans++;
                }
                else if(i==n-1-i || j==m-1-j){
                    if(a==4)a1++;
                    if(a==2)diff++;
                }
                else{
                    if(a==4 || a==0)ans+=0;
                    else if (a==3 || a==1)ans+=1;
                    else ans+=2;
                }
            }
        }
        if(diff==0&&a1%2==1)ans+=2;
        ans+=diff;
        return ans;
    }
};
