class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                tmp = grid[i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][j] + grid[m - 1 - i][n - 1 - j]
                if tmp in (1, 3):
                    ans += 1
                elif tmp == 2:
                    ans += 2
        ttmp_m = 0
        cnt1_m = 0
        if m % 2:
            for j in range(n // 2):
                if grid[m // 2][j] != grid[m // 2][n - 1 - j]:
                    ttmp_m += 1
                    cnt1_m += 2
                elif grid[m // 2][j]:
                    cnt1_m += 2
        ttmp_n = 0
        cnt1_n = 0
        if n % 2:
            for i in range(m // 2):
                if grid[i][n // 2] != grid[m - 1 - i][n // 2]:
                    ttmp_n += 1
                    cnt1_n += 2
                elif grid[i][n // 2]:
                    cnt1_n += 2

        ttmp = ttmp_m + ttmp_n
        if (cnt1_m + cnt1_n) % 4 and not ttmp:
            ttmp += 2
        ans += ttmp

        if m % 2 and n % 2:
            ans += grid[m // 2][n // 2]
        return ans
