class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m , n = len(grid), len(grid[0])
        ans = 0
        if m % 2 == n %2 == 0:
            for i in range(m//2):
                for j in range(n // 2):
                    a,b,c,d = grid[i][j], grid[i][~j], grid[~i][j],grid[~i][~j]
                    v = Counter([a,b,c,d])
                    v = max(v[0], v[1])
                    ans += 4 - v
        elif m % 2 == n %2 == 1:
            for i in range(m//2):
                for j in range(n // 2):
                    a,b,c,d = grid[i][j], grid[i][~j], grid[~i][j],grid[~i][~j]
                    v = Counter([a,b,c,d])
                    v = max(v[0], v[1])
                    ans += 4 - v
            c1 = tans = 0
            for i in range(n // 2):
                if grid[m//2][i] != grid[m//2][~i]:
                    tans += 1
                elif grid[m//2][i] == grid[m//2][~i] == 1:
                    c1 += 2
            for j in range(m // 2):
                if grid[j][n//2] != grid[~j][n//2]:
                    tans += 1
                elif grid[j][n//2] == grid[~j][n//2] == 1:
                    c1 += 2
            if c1 % 4 != 0:
                if tans == 0:
                    ans += 2
            ans += tans   
            if grid[m//2][n//2] == 1:   # 中心 一定
                ans += 1
        elif  m % 2 == 1:
            for i in range(m//2):
                for j in range(n // 2):
                    a,b,c,d = grid[i][j], grid[i][~j], grid[~i][j],grid[~i][~j]
                    v = Counter([a,b,c,d])
                    v = max(v[0], v[1])
                    ans += 4 - v
            c1 = tans = 0
            for i in range(n // 2):
                if grid[m//2][i] != grid[m//2][~i]:
                    tans += 1
                elif grid[m//2][i] == grid[m//2][~i] == 1:
                    c1 += 2
            if c1 % 4 != 0:
                if tans == 0:
                    ans += 2
            ans += tans   
        else:
            for i in range(m//2):
                for j in range(n // 2):
                    a,b,c,d = grid[i][j], grid[i][~j], grid[~i][j],grid[~i][~j]
                    v = Counter([a,b,c,d])
                    v = max(v[0], v[1])
                    ans += 4 - v
            c1 = tans = 0
            for j in range(m // 2):
                if grid[j][n//2] != grid[~j][n//2]:
                    tans += 1
                elif grid[j][n//2] == grid[~j][n//2] == 1:
                    c1 += 2
            if c1 % 4 != 0:
                if tans == 0:
                    ans += 2
            ans += tans 

        return ans
