class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {


        int n = grid.size();
        int m = grid[0].size();

        int ans = 0;
        int f1 = 0 , f2 = 0, f3 = 0;

        for(int i = 0 ; i < (n+1)/2;  i ++){
            for(int j = 0 ; j  < (m+1)/2 ; j ++){

                set<pair<int , int>> pt;
                pt.insert({i,j});
                pt.insert({n-1-i,j});
                pt.insert({i,m-1-j});
                pt.insert({n-1-i,m-1-j});

                int oc = 0 , zc = 0;
                for(auto kk : pt){
                    if(grid[kk.first][kk.second]){
                        oc++;
                    }
                    else{
                        zc++;
                    }
                }

                if(pt.size() == 4){
                    ans += min(oc , zc);
                }
                else{
                    if(pt.size() == 1)
                    {
                        ans+= oc;
                    }
                    else
                    {
                        if(oc == zc) f3++;
                        else if(zc == 0) f1++;
                        else f2++;
                    }
                }
            }
        }

        if(f3){
            ans += f3;
        }
        else{
            if(f1%2 != 0){
                ans += 2;
            }
        }

        return ans;

    }
};
