from collections import defaultdict

class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        n = len(grid)
        m = len(grid[0])

        vis = [[0 for i in range(m)] for j in range(n)]

        carr = defaultdict(list)

        for i in range(n):


            for j in range(m):


                if vis[i][j] == 0:

                    set1 = set()
                    p1 = (n-1-i,j)
                    p2 = (i, m-1-j)
                    p3 = (n-i-1, m-j-1)
                    p4 = (i,j)

                    vis[p1[0]][p1[1]] = 1
                    vis[p2[0]][p2[1]] = 1
                    vis[p3[0]][p3[1]] = 1
                    vis[p4[0]][p4[1]] = 1

                    set1.add(p1)
                    set1.add(p2)
                    set1.add(p3)
                    set1.add(p4)

                    cnt0 = 0
                    cnt1 = 0
                    for x in set1:
                        if grid[x[0]][x[1]] == 1:
                            cnt1 += 1
                        else:
                            cnt0 += 1

                    carr[len(set1)].append((cnt0, cnt1))

        ans = 0

        if 4 in carr:
            for x in carr[4]:

                ans += min(x[0], x[1])

        if 1 in carr:

            cnt0 = 0
            cnt1 = 0

            for x in carr[1]:
                cnt0 += x[0]
                cnt1 += x[1]

            rem = cnt1%4

            a1 = 10**9

            if cnt0 >= 4 - rem:
                a1 = 4 - rem

            a2 = rem

            ans += min(a2, a1)

        if 2 in carr:

            cnt = 0

            for x in carr[2]:
                if x[0] == x[1]:
                    cnt += 1

            one = 0

            for x in carr[2]:

                if x[1] == 2:
                    one += 1

            if one%2 != 0:
                if cnt == 0:
                    ans += 2

            ans += cnt

        return ans
