class Solution(object):
    def minFlips(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        m = len(grid)
        n = len(grid[0])
        count = 0
        for i in range(m//2):
            for j in range(n//2):
                count += min(4-(grid[i][j]+grid[m-i-1][j]+grid[i][n-j-1]+grid[m-i-1][n-j-1]),grid[i][j]+grid[m-i-1][j]+grid[i][n-j-1]+grid[m-i-1][n-j-1])

        count2 =0
        con = 0
        if m%2:
            for i in range(n//2):
                count2+=grid[(m)//2][i]^grid[(m)//2][n-i-1]
                con+=grid[(m)//2][i]&grid[(m)//2][n-i-1]
        if n%2:
            for i in range(m//2):
                count2+=grid[i][(n)//2]^grid[m-i-1][(n)//2]
                con+=grid[i][(n)//2]&grid[m-i-1][(n)//2]
        # print(con, count, count2)

        if m%2 and n%2:
            count+=grid[m//2][n//2]

        if (con)%2 and count2==0:
            return count+count2+2
        else:
            return count+count2
