class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int res1 = 0;
        for (int i = 0; i < m/2; ++i) {
        	for (int j = 0; j < n/2; ++j) {
        		int count = 0;
        		count = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j];
        		if (count ==1 || count==3) {
        			res1++;
				} else if (count == 2) {
					res1 = res1+ 2;
				}
			}
		}

		int res2 = 0;
		int one2 = 0;
		if (m % 2 == 1) {
			for (int i=0; i < n/2; ++i) {
				if (grid[m/2][i] + grid[m/2][n-1-i] == 1) {
					res2++;
				} else if (grid[m/2][i] + grid[m/2][n-1-i] == 2) {
					one2=one2+2;
				}
			}
		}

		int res3 = 0;
		int one3 = 0;
		if (n % 2 == 1) {
			for (int i=0; i < m/2; ++i) {
				if (grid[i][n/2] + grid[m-1-i][n/2] == 1) {
					res3++;
				} else if (grid[i][n/2] + grid[m-1-i][n/2] == 2) {
					one3=one3+2;
				}
			}
		}

		int res=0;
		if ( (one2+one3) % 4 != 0) {
			if (res2+res3 == 0) {
				res=res+2;
			}
		} 
		if (m%2 == 1 && n%2 ==1 && grid[m/2][n/2] == 1) {
			res++;
		}

		res=res+res1+res2+res3;
		return res;
    }
};
