class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int x = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] +
                        grid[n - 1 - i][m - 1 - j];
                ans += min(x, 4 - x);
            }
        }
        int flips = 0, one = 0;
        if (n & 1 && m & 1) {
            for (int j = 0; j < m / 2; ++j) {
                one += grid[n / 2][j] + grid[n / 2][m - 1 - j];
                if (grid[n / 2][j] != grid[n / 2][m - 1 - j]) {
                    flips++;
                    ans++;
                }
            }
            for (int i = 0; i < n / 2; ++i) {
                one += grid[i][m / 2] + grid[n - 1 - i][m / 2];
                if (grid[i][m / 2] != grid[n - 1 - i][m / 2]) {
                    flips++;
                    ans++;
                }
            }
            if (grid[n / 2][m / 2] == 1)
                ans++;

        } else if (n & 1) {
            for (int j = 0; j < m / 2; ++j) {
                one += grid[n / 2][j] + grid[n / 2][m - 1 - j];
                if (grid[n / 2][j] != grid[n / 2][m - 1 - j]) {
                    flips++;
                    ans++;
                }
            }
        } else if (m & 1) {
            for (int i = 0; i < n / 2; ++i) {
                one += grid[i][m / 2] + grid[n - 1 - i][m / 2];
                if (grid[i][m / 2] != grid[n - 1 - i][m / 2]) {
                    flips++;
                    ans++;
                }
            }
        }
        one = one % 4;
        one = min(4 - one, one);
        if (flips < one) {
            ans += one - flips;
        }
        return ans;
    }
};
