class Solution {
public:

    ll fun(v<v<ll>> &a)
    {
        ll n=a.size();
        ll m=a[0].size();
        ll ans=0;
        for(ll i=0;i<n/2;i++)
        {
            for(ll j=0;j<m/2;j++)
            {
                ll c1=a[i][j];
                ll c2=a[n-1-i][j];
                ll c3=a[i][m-1-j];
                ll c4=a[n-1-i][m-1-j];
                v<ll> m(2,0);
                m[c1]++;
                m[c2]++;
                m[c3]++;
                m[c4]++;
                if(m[0]>m[1])
                {
                    ans+=m[1];   
                }
                else if(m[1]>m[0])
                {
                    ans+=m[0];
                }
                else 
                {
                    ans+=m[1];
                }
            }
        }
        ll c0=0;
        ll c1=0;
        ll c01=0;
        if(n%2==1)
        {
            for(ll j=0;j<m/2;j++)
            {
                ll e1=a[n/2][j];
                ll e2=a[n/2][m-1-j];
                if(e1==e2)
                {
                    if(e1==0)
                        c0++;
                    else 
                        c1++;        
                }
                else
                {
                    c01++;
                }

            }
        }
        if(m%2==1)
        {
            for(ll i=0;i<n/2;i++)
            {
                ll e1=a[i][m/2];
                ll e2=a[n-1-i][m/2];
                if(e1==e2)
                {
                    if(e1==0)
                        c0++;
                    else 
                        c1++;        
                }
                else
                {
                    c01++;
                }
            }
        }
        c1=(2*c1)%4;
        c1=c1/2;
        if(c01>=c1)
        {
            ans+=c01;
        }
        else 
        {
            ans+=c01;
            c1-=c01;
            ans+=2*c1;
        }
        if((n&1)==1&&(m&1)==1)
        {
            ans+=a[n/2][m/2]==1;
        }
        return ans;
    }

    int minFlips(vector<vector<int>>& a) {
        return fun(a);
    }
};
