class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int ones=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                ans+=min(4-ones,ones);
            }
        }
        int x=0,cnt1=0;
        if(m%2==1){
            for(int j=0;j<n/2;j++){
                if(grid[m/2][j]!=grid[m/2][n-1-j]) ans++,cnt1+=2,x++;
                else{

                    if(grid[m/2][j]) cnt1++;
                if(grid[m/2][n-1-j]) cnt1++;
                }

            }
        }
        if(n%2==1){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-1-i][n/2]) ans++,cnt1+=2,x++;
                else{
                    if(grid[i][n/2]) cnt1++;
                    if(grid[m-1-i][n/2]) cnt1++;
                }

            }
        }
        if(cnt1%4!=0){

            if(x<=0)ans+=cnt1%4;
        }
        if(n%2 && m%2 && grid[m/2][n/2]) ans++;
        return ans;
    }
};
