class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int a = grid.size();
        int b = grid[0].size();
        int s = 0;

        int ans1 = 0;
        int ans2=0;
        int q = 0, t=0;
        for (int i=0;i<=a-1-i;i++) {
            for (int j=0;j<=b-1-j;j++) {
                if (i!=a-1-i && j!=b-1-j) {
                    s = grid[i][j]+grid[i][b-1-j]+grid[a-1-i][j] + grid[a-1-i][b-1-j];
                    ans1+=min(s, 4-s);
                } else if (i!=a-1-i || j!=b-1-j) {
                    s = grid[i][j]+grid[i][b-1-j]+grid[a-1-i][j] + grid[a-1-i][b-1-j]; // 0 2 4
                    if (s==4) q++;
                    if (s==2) { ans1++; t++; }
                } else {
                    ans1+=grid[i][j];
                }
            }
        }
        if (t==0 && q%2==1) {ans1+=2;}
        return ans1;

    }
};
