class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int n = arr.size(), m = arr[0].size();
        if (n == 1 && m == 1)
            return arr[0][0];
        // if (n == 1) {
        //     int cnt = 0, one_cnts = 0;
        //     for (int j = 0; j < m/2; ++j) {
        //         cnt += (arr[0][j] != arr[0][m-j-1]);
        //         if (arr[0][j] == arr[0][m-j-1] && arr[0][j] == 1)
        //             one_cnts += 2;
        //     }
        //     if (one_cnts % 4 == 0)
        //         return cnt;
        //     if (one_cnts % 4 == 2) {
        //         return (cnt > 0 ? cnt : 2);
        //     }
        // }
        // if (m == 1) {
        //     int cnt = 0, one_cnts = 0;
        //     for (int i = 0; i < n/2; ++i) {
        //         cnt += (arr[i][0] != arr[n-i-1][0]);
        //         if (arr[i][0] == arr[n-i-1][0] && arr[i][0] == 1)
        //             one_cnts += 2;
        //     }
        //     if (one_cnts % 4 == 0)
        //         return cnt;
        //     if (one_cnts % 4 == 2) {
        //         return (cnt > 0 ? cnt : 2);
        //     }
        // }

        int res = 0;
        for (int i = 0; i < n/2; ++i) {
            for (int j = 0; j < m/2; ++j) {
                int cnt0 = 0, cnt1 = 0;
                arr[i][j]           ? ++cnt1 : ++cnt0;
                arr[i][m-j-1]       ? ++cnt1 : ++cnt0;
                arr[n-i-1][j]       ? ++cnt1 : ++cnt0;
                arr[n-i-1][m-j-1]   ? ++cnt1 : ++cnt0;

                if (cnt0 == 4 || cnt1 == 4)
                    continue;
                res += min(cnt0, cnt1);
            }
        }

        int cnts = 0, one_cnts = 0;
        if (n % 2) {
            for (int j = 0; j < m/2; ++j) {
                cnts += (arr[n/2][j] != arr[n/2][m-j-1]);
                if (arr[n/2][j] == arr[n/2][m-j-1] && arr[n/2][j] == 1)
                    one_cnts += 2;
            }
        }

        if (m % 2) {
            for (int i = 0; i < n/2; ++i) {
                cnts += (arr[i][m/2] != arr[n-i-1][m/2]);
                if (arr[i][m/2] == arr[n-i-1][m/2] && arr[i][m/2] == 1)
                    one_cnts += 2;
            }
        }

        if (one_cnts % 4 == 2 && cnts <= 0)
            cnts += 2;

        int middle_one = 0;
        if (n%2 && m%2)
            middle_one = arr[n/2][m/2];

        res += cnts + middle_one;

        return res;
    }
};
