class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        if(n*m==1){
            return grid[0][0];
        }
        vector<vector<int>>vis(n,vector<int>(m,0));
        int ans=0,one=0,two=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]) continue;
                for(int x:{i,n-1-i}){
                    for(int y:{j,m-1-j}) vis[x][y]=1;
                }
                int z=0,o=0;
                if(2*i!=n-1 && 2*j!=m-1){
                    for(int x:{i,n-1-i}){
                        for(int y:{j,m-1-j}){

                            if(grid[x][y]==0) z++;
                            else o++;
                        }
                    }
                    ans+=min(z,o);
                }
                else if(2*i==n-1){
                    if(2*j==m-1){
                        if(grid[i][j]==1) ans++;
                    }
                    else if(grid[i][j]==grid[i][m-1-j]){
                        if(grid[i][j]==1) two++;
                    }
                    else ans++,one++;
                }
                else{
                    if(grid[i][j]==grid[n-1-i][j]){
                        if(grid[i][j]==1) two++;
                    }
                    else ans++,one++;
                }
            }
        }
        if(two%2){
            if(one) ;
            else ans+=2;
        }
        return ans;
    }
};
