class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;

        int cnt = 0;
        int one = 0;
        int eq = 0;
        for (int i = 0; i <= (m - 1) / 2; i++) {
            for (int j = 0; j <= (n - 1) / 2; j++) {

                if (i == m - i - 1 && j == n - j - 1) {
                    if (grid[i][j] == 1) {
                        cnt++;
                    }
                } else if (i == m - i - 1) {
                    int a = grid[i][j];
                    int b = grid[i][n - j - 1];
                    if (a == b) {
                        if (a == 1) {
                            one += 2;
                        }
                    } else {
                        cnt++;
                        eq++;
                    }

                } else if (j == n - j - 1) {
                    int a = grid[i][j];
                    int c = grid[m - i - 1][j];

                    if (a == c) {
                        if (a == 1) {
                            one += 2;
                        }
                    } else {
                        cnt++;
                        eq++;
                    }

                } else {
                    int a = grid[i][j];
                    int b = grid[i][n - j - 1];
                    int c = grid[m - i - 1][j];
                    int d = grid[m - i - 1][n - j - 1];

                    int[] x = new int[2];
                    x[a]++;
                    x[b]++;
                    x[c]++;
                    x[d]++;

                    if (x[1] == 1 || x[1] == 3) {
                        cnt++;
                    } else if (x[1] == 2) {
                        cnt += 2;
                    }
                }
            }
        }

        if (one % 4 == 0 || eq > 0) {
            return cnt;
        }
        return cnt + 2;
    }
}
