class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int r=n/2;
        int c=m/2;

        int cnt=0;

        for(int i=0;i<r;++i){
            for(int j=0;j<c;++j){
                int cnt0=0,cnt1=0;

                if(grid[i][j])++cnt1;
                else cnt0++;

                if(grid[i][m-1-j])++cnt1;
                else cnt0++;
                if(grid[n-1-i][j])++cnt1;
                else cnt0++;
                if(grid[n-1-i][m-1-j])++cnt1;
                else cnt0++;
                cnt+=min(cnt0,cnt1);
            }
        }
        int cnt1=0;
        int cnt1_ireg=0;
        if(n%2){
            for(int j=0;j<m/2;++j)
            if(grid[n/2][j]!=grid[n/2][m-1-j]){++cnt;cnt1_ireg++;}
            else if(grid[n/2][m-1-j]==1)cnt1+=2;
        }
        if(m%2){
            for(int i=0;i<n/2;++i)
            if(grid[i][m/2]!=grid[n-1-i][m/2]){++cnt;cnt1_ireg++;}
            else if(grid[i][m/2]==1)cnt1+=2;
        }
        if(cnt1%4) {
            if(!cnt1_ireg)cnt+=2;
        }
        if(n%2 and m%2 and grid[n/2][m/2]==1)++cnt;

        return cnt;
    }
};
