class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;

        int r = m >> 1;
        int d = n >> 1;
        for(int i = 0; i < r; ++i){
            for(int j = 0; j < d; ++j){
                int tmp = grid[i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][j] + grid[m - 1 - i][n - 1 - j];

                ans += min(4 - tmp , tmp);
            }
        }

        int t = 0;
        int o = 0;
        if((m & 1) && (n & 1)){
            for(int i = 0; i < r; ++i){
                int tmp = grid[i][d] + grid[m - 1 - i][d];
                if(tmp == 2) ++t;
                else if(tmp == 1) ++o;

                ans += min(2 - tmp, tmp);
            }
            for(int j = 0; j < d; ++j){
                int tmp = grid[r][j] + grid[r][n - 1 - j];
                if(tmp == 2) ++t;
                else if(tmp == 1) ++o;

                ans += min(2 - tmp, tmp);
            }

            ans += grid[r][d];
        }
        else if(m & 1){
            for(int j = 0; j < d; ++j){
                int tmp = grid[r][j] + grid[r][n - 1 - j];
                if(tmp == 2) ++t;
                else if(tmp == 1) ++o;

                ans += min(2 - tmp, tmp);
            }
        }
        else if(n & 1){
            for(int i = 0; i < r; ++i){
                int tmp = grid[i][d] + grid[m - 1 - i][d];
                if(tmp == 2) ++t;
                else if(tmp == 1) ++o;

                ans += min(2 - tmp, tmp);
            }
        }
        if((t % 2) && o == 0) ans += 2;

        return ans;
    }
};
