class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int c=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                vector<int> v(2,0);
                v[grid[i][j]]++;
                v[grid[n-1-i][j]]++;
                v[grid[n-i-1][m-j-1]]++;
                v[grid[i][m-j-1]]++;
                if(v[0]>v[1]){
                    c+=v[1];
                }
                else
                 c+=v[0];
            }
        }
        if(n%2==0 && m%2==0)
        return c;
        int o=0;
        int ns=0;
        if(n%2==1){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-1-j]){
                    c++;ns++;
                }
                else if(grid[n/2][j]==1)
                 o++;
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]){
                    c++;ns++;
                }
                else if(grid[i][m/2]==1)
                 o++;
            }
        }
        if(ns==0 && o%2==1)
            c+=2;
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1)
         c++;

        return c;
    }
};
