class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int n=arr.size();
        int m=arr[0].size();
        // int arr[n][m];
        // memset(arr,0,sizeof(arr));

        int p=0,zeroone=0,ans=0;
        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                map<pair<int,int>,int> mp;
                mp[{i,j}]++;
                mp[{n-1-i,j}]++;
                mp[{n-1-i,m-1-j}]++;
                mp[{i,m-1-j}]++;
                // cout<<mp.size()<<" ";
                int one=0,zero=0;
                for(auto it: mp){
                    if(arr[it.first.first][it.first.second]==0){
                        zero++;
                    }
                    else{
                        one++;
                    }
                }
                // cout<<zero<<" "<<one<<endl;
                if(mp.size()==4){
                    ans+=min(zero,one);
                }
                else if(mp.size()==2){
                    if(zero==0){
                        p++;
                    }
                    else{
                        if(zero==1 && one==1){
                            ans++;
                            zeroone++;
                        }
                    }
                }
                else{
                    ans+=one;
                }    
            }
        }
        // cout<<endl;
        cout<<p<<endl;

        if(p%2==0){
            return ans;
        }
        else{
            if(zeroone>0){
                return ans;
            }
            else{
                return ans+2;
            }
        }
        return 0;
    }
};
