class Solution {
    public int minFlips(int[][] grid) {
        int c1=0;
        int c2=0;
        int m=grid.length;
        int n=grid[0].length;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){

                int sum=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                c1+=Math.min(4-sum,sum);
            }
        }
        if(m%2==1 && n%2==1){
            c1+=grid[m/2][n/2];
        }
            int co=0;
            int cv=0;
            int ch=0;
            int ck=0;
        if(m%2==1){
            int i=m/2;
            for(int j=0;j<n/2;j++){
                if(grid[i][j]!=grid[i][n-1-j]){
                    co++;
                }else
                    cv+=grid[i][j]+grid[i][n-1-j];
            }
        }
        if(n%2==1){
            int j=n/2;
            for(int i=0;i<m/2;i++){
                if(grid[i][j]!=grid[m-1-i][j]){
                    ch++;
                }else
                    ck+=grid[i][j]+grid[m-1-i][j];
            }
        }
        ck+=cv;
        ch+=co;
        c1+=ch;
        if(ck%4!=0){
            if(ch==0){
                c1+=2;
            }
        }
        return c1;
    }
}
