class Solution {
public:
    int minFlips(vector<vector<int>>& g) {

       int n = g.size();
       int m = g[0].size();
       int ans =0;
       for(int i=0;i<n/2;i++)
       {
           for(int j=0;j<m/2;j++)
           {


              if(i==(n-1-i))
              continue;
              if(j==(m-1-j))
              continue;

              int add = g[i][j] + g[n-1-i][j]+ g[n-1-i][m-1-j] + g[i][m-1-j];
              ans+= min(add,4-add);

           }
       }

       int m1 = n/2;
       int m2 = m/2;

       if(n%2&&m%2&&g[m1][m2])
       {
          ans+=1;
       }

     //  cout<<ans<<endl;
       int poo=0;
       int cnt =0;
       if(n%2)
       {
          for(int j=0;j<m/2;j++)
          {
            if(g[m1][j]!=g[m1][m-1-j])
            cnt++;
            if(g[m1][j]==g[m1][m-1-j]&&g[m1][m-1-j])
            poo++;
          }
       }

       if(m%2)
       {
          for(int i=0;i<n/2;i++)
          {
            if(g[i][m2]!=g[n-1-i][m2])
            cnt++;
            if(g[i][m2]==g[n-1-i][m2]&&g[i][m2])
            poo++;
          }
       }
     //   cout<<poo<<' '<<ans<<endl;
       poo%=2;

       if(cnt>=1)
       ans+=cnt;
       else
       ans+=(cnt+(2*poo));

       return ans;

    }
};
