class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        for (int i = 0; i < (int)grid.size() / 2; i++) {
            for (int j = 0; j < grid[i].size() / 2; j++) {
                int h = grid[i][j] + grid[i][(int)grid[i].size() - j - 1] + grid[(int)grid.size() - i - 1][j] + grid[(int)grid.size() - i - 1][(int)grid[i].size() - j - 1];
                res += min(h, 4 - h);
            }
        }

        if (grid.size() % 2 == 1 && grid[0].size() % 2 == 1 && grid[grid.size() / 2][grid[0].size() / 2] == 1) {
            res++;
        }

        if (grid.size() % 2 == 1 && grid[0].size() % 2 == 1) {
            bool st = false;
            int all = 0;
            for (int i = 0; i < grid.size() / 2; i++) {
                int h = grid[i][grid[0].size() / 2] + grid[(int)grid.size() - i - 1][grid[0].size() / 2];
                if (h == 1) {
                    res++;
                    st = true;
                } else if (h == 2) {
                    all += 2;
                }
            }

            for (int i = 0; i < grid[0].size() / 2; i++) {
                int h = grid[grid.size() / 2][i] + grid[grid.size() / 2][grid[0].size() - i - 1];
                if (h == 1) {
                    res++;
                    st = true;
                } else if (h == 2) {
                    all += 2;
                }
            }

            if (!st && all % 4 != 0) {
                res += 2;
            }
        } else if (grid.size() % 2 == 1) {
            bool st = false;
            int all = 0;
            for (int i = 0; i < grid[0].size() / 2; i++) {
                int h = grid[grid.size() / 2][i] + grid[grid.size() / 2][grid[0].size() - i - 1];
                if (h == 1) {
                    res++;
                    st = true;
                } else if (h == 2) {
                    all += 2;
                }
            }

            if (!st && all % 4 != 0) {
                res += 2;
            }
        } else if (grid[0].size() % 2 == 1) {
            bool st = false;
            int all = 0;
            for (int i = 0; i < grid.size() / 2; i++) {
                int h = grid[i][grid[0].size() / 2] + grid[(int)grid.size() - i - 1][grid[0].size() / 2];
                if (h == 1) {
                    res++;
                    st = true;
                } else if (h == 2) {
                    all += 2;
                }
            }
            if (!st && all % 4 != 0) {
                res += 2;
            }
        }

        return res;
    }
};
