class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=a.size();
        int m=a[0].size();
        int ans=0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int x=a[i][j]+a[i][m-j-1]+a[n-i-1][j]+a[n-i-1][m-j-1];
                ans+=min(x,4-x);
            }
        }
        if(n%2!=0 && m%2!=0)ans+=(a[n/2][m/2]);
        int x=0, y=0, z=0;   
        if(n%2!=0){
            for(int j=0; j<m/2; j++){
                if(a[n/2][j]==a[n/2][m-j-1]){
                    if(a[n/2][j]==1)x++; 
                }
                else y++;
            }
        }
        if(m%2!=0){
            for(int i=0; i<n/2; i++){
                if(a[i][m/2]==a[n-i-1][m/2]){
                    if(a[i][m/2]==1)x++;
                }
                else y++;
            }
        }
        x*=2;
        if(x%4==0)ans+=y;
        else{
            if(y>0)ans+=y;
            else ans+=2;
        }
        return ans;
    }
};
