class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:


        n = len(grid)
        m = len(grid[0])

        res = 0

        if n%2 == 1 and m%2 == 1:
            res += grid[n//2][m//2]
            grid[n//2][m//2] = 0


        for i in range(n//2):
            for j in range(m//2):
                val = grid[i][j] + grid[~i][j] + grid[i][~j] + grid[~i][~j]
                res += min(val, 4-val)
                grid[i][j] = 0
                grid[~i][j] = 0
                grid[i][~j] = 0
                grid[~i][~j] = 0

        arr = []
        brr = []

        if n%2 == 1:
            i = n//2
            for j in range(m//2):
                arr.append(grid[i][j])
                brr.append(grid[i][~j])

        if m%2 == 1:
            j = m//2
            for i in range(n//2):
                arr.append(grid[i][j])
                brr.append(grid[~i][j])

        if len(arr) == 1:
            res += sum(arr) + sum(brr)
            return res

        total = sum(arr) + sum(brr)
        val = 0

        for a,b in zip(arr, brr):            
            if a != b:
                res += 1
                val += 1

        if val == 0 and total % 4 == 2:
            res += 2

        return res
