#define pii pair<int, int>
class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int r = grid.size(), c = grid.front().size(), ans = 0;
        for(int i = 0; i < (r) / 2; i++){
            for(int j = 0; j < (c) / 2; j++){
                pii cor = {i, j};
                pii corR = {i, c - j - 1};
                pii corD = {r - i - 1, j};
                pii corRD = {r - i - 1, c - j - 1};
                // printf("%d %d %d %d\n", cor.second, corR.second, corD.second, corRD.second);
                auto ones = grid[i][j] + grid[corR.first][corR.second] * (corR != cor) + grid[corD.first][corD.second] * (corD != cor) + grid[corRD.first][corRD.second] * (corRD != cor);

                // set<pii>  coords;
                // coords.insert({i, j});
                // coords.insert(corR);
                // coords.insert(corD);
                // coords.insert(corRD);
                // printf("%d %d\n", ones, coords.size());
                int zs = 4 - ones;
                ans += min(zs, ones);
            }
        }

        auto rowMid = r & 1, colMid = c & 1;
        int p = 0, dif = 0, mc = c / 2, mr = r / 2;
        if(rowMid){
            for(int j = 0; j < c / 2; j++){
                p += (grid[mr][j] == grid[mr][c - j - 1] and grid[mr][j] == 1);
                dif += (grid[mr][j] != grid[mr][c - j - 1]);
            }

        }
        if(colMid){
            for(int i = 0; i < r / 2; i++){
                p += (grid[i][mc] == grid[r - i - 1][mc] and grid[i][mc] == 1);
                dif += (grid[i][mc] != grid[r - i - 1][mc]);
            }

        }
        ans += (rowMid && colMid && grid[mr][mc] == 1);
        if(p & 1){
            if(dif){
                ans += dif;
            }
            else{
                ans += 2;
            }
        }
        else{
            ans += dif;
        }
        return ans;
    }
};
