class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(); int m = grid[0].size();
        int up= 0, down = n-1, left = 0, right = m-1;
        int flips = 0;
        while(up < down){
            left = 0, right = m-1;
            while(left < right){
                int zeros = 0, ones = 0;
                if(grid[up][left] == 0)
                    zeros++;
                else
                    ones++;
                if(grid[up][right] == 0)
                    zeros++;
                else
                    ones++;
                if(grid[down][left] == 0)
                    zeros++;
                else
                    ones++;
                if(grid[down][right] == 0)
                    zeros++;
                else
                    ones++;
                if(zeros == 3 or zeros == 1)
                    flips++;
                else if(zeros ==2)
                    flips+=2;
                left++;  right--;
            }
            up++; down--;
        }
        int oo = 0, zo = 0;
        if(n%2 != 0){
            int i = n/2;
            int left = 0, right = m-1;
            while(left < right){

                if(grid[i][left] == 1 and grid[i][right] == 1)
                    oo++;
                else if(grid[i][left] == 0 and grid[i][right] == 0);
                else{
                    zo++;
                    flips++;
                }
                left++;  right--;
            }
        }
        if(m%2 != 0){
            int i = m/2;
            int left = 0, right = n-1;
            while(left < right){
                if(grid[left][i] == 1 and grid[right][i] == 1)
                    oo++;
                else if(grid[left][i] == 0 and grid[right][i] == 0);
                else{
                    zo++;
                    flips++;
                }
                left++;  right--;
            }
        }
        if(oo % 2!= 0){
            if(zo == 0){
                flips+=2;
            }
        }
        if(n %2 !=0  and m%2 != 0 and grid[n/2][m/2] == 1){
            flips++;

        }
        return flips;
    }
};
