#include <bits/stdc++.h>
using namespace std;

class Solution
{
public:
    int minFlips(vector<vector<int>> &mat)
    {
        int n = mat.size(), m = mat[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; ++i)
        {
            for (int j = 0; j < m / 2; ++j)
            {
                int cnt = 0;
                cnt += mat[i][j];
                cnt += mat[i][m - 1 - j];
                cnt += mat[n - 1 - i][j];
                cnt += mat[n - 1 - i][m - 1 - j];
                ans += min(cnt, 4 - cnt);
            }
        }
        // for the odd rows and odd columns.
        int cnt = 0;
        int matching_ones = 0;
        if (n & 1)
        {
            int curr = n / 2;
            for (int i = 0; i < m / 2; ++i)
            {
                if (mat[curr][i] != mat[curr][m - 1 - i])
                {
                    cnt++;
                }
                else if (mat[curr][i] == 1)
                {
                    matching_ones += 2;
                }
            }
        }
        if (m & 1)
        {
            int curr = m / 2;
            for (int i = 0; i < n / 2; ++i)
            {
                if (mat[i][curr] != mat[n - 1 - i][curr])
                {
                    cnt++;
                }
                else if (mat[i][curr] == 1)
                {
                    matching_ones += 2;
                }
            }
        }
        matching_ones = matching_ones % 4;
        if (cnt == 0)
        {
            ans += matching_ones;
        }
        else
        {
            ans += cnt;
        }
        if ((n & 1) && (m & 1))
        {
            ans += mat[n / 2][m / 2];
        }
        return ans;
    }
};
