class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        ones = 0
        oneOpt = twoOpt = 0
        moves1 = moves2 = moves4 = 0

        for r in range(m):
            for c in range(n):
                if 2*r+1 == m and 2*c+1 == n:
                    if grid[r][c]:
                        moves1 += 1
                elif 2*r+1 == m or 2*c+1 == n:
                    oR = m - r - 1
                    oC = n - c - 1
                    newOnes = grid[r][c] + grid[oR][c] + grid[r][oC] + grid[oR][oC]
                    newOnes //= 2
                    if newOnes == 1:
                        oneOpt += 1
                        moves2 += 1
                    if newOnes == 2:
                        ones += 2
                else:
                    oR = m - r - 1
                    oC = n - c - 1
                    newOnes = grid[r][c] + grid[oR][c] + grid[r][oC] + grid[oR][oC]
                    if newOnes == 1:
                        moves4 += 1
                    if newOnes == 2:
                        moves4 += 2
                    if newOnes == 3:
                        moves4 += 1

        ones //= 2

        moves2 //= 2
        moves4 //= 4

        if ones % 4 != 0 and oneOpt == 0:
            moves1 += 2

        return moves1 + moves2 + moves4
