class Solution {
public:
    int minFlipsToMakePalindromic(vector<int>& line) {
    int n = line.size();
    int flips = 0;
    for (int i = 0; i < n / 2; ++i) {
        if (line[i] != line[n - i - 1]) {
            flips++;
        }
    }
    return flips;
}
    int minFlips(vector<vector<int>>& grid) {
    int rows = grid.size();
    int cols = grid[0].size();
    int flipCount = 0;

    int left, right;

    // Check and flip necessary cells in the grid to make rows and columns palindromic
    for (int i = 0; i < rows / 2; i++) {
        left = 0;
        right = cols - 1;

        while (left < right) {
            int zeroCount = 0, oneCount = 0;
            if (grid[i][left]) oneCount++; else zeroCount++;
            if (grid[i][right]) oneCount++; else zeroCount++;
            if (grid[rows - 1 - i][left]) oneCount++; else zeroCount++;
            if (grid[rows - 1 - i][right]) oneCount++; else zeroCount++;

            flipCount += min(zeroCount, oneCount);
            left++;
            right--;
        }
    }

    int singleFlips = 0, doubleFlips = 0;

    if (cols % 2) {
        left = 0;
        right = rows - 1;
        while (left < right) {
            if (grid[left][cols / 2] != grid[right][cols / 2]) {
                singleFlips++;
            } else {
                if (grid[left][cols / 2] == 1) {
                    doubleFlips++;
                }
            }
            left++;
            right--;
        }
    }

    if (rows % 2) {
        left = 0;
        right = cols - 1;
        while (left < right) {
            if (grid[rows / 2][left] != grid[rows / 2][right]) {
                singleFlips++;
            } else {
                if (grid[rows / 2][left] == 1) {
                    doubleFlips++;
                }
            }
            left++;
            right--;
        }
    }

    if (rows % 2 && cols % 2) {
        if (grid[rows / 2][cols / 2]) {
            flipCount++;
        }
    }

    doubleFlips %= 2;
    flipCount += singleFlips;
    if (doubleFlips) {
        if (singleFlips == 0) {
            flipCount += 2;
        }
    }

    return flipCount;
}

};
