class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt0 =0 , cnt1 = 0;
                (grid[i][j]? cnt1 : cnt0) ++;
                (grid[n-1-i][j]? cnt1 : cnt0) ++;
                (grid[n-1-i][m-1-j]? cnt1 : cnt0) ++;
                (grid[i][m-1-j]? cnt1 : cnt0) ++;
                ans += min(cnt1,cnt0);
            }
        }

        int t0 = 0, t1 = 1e9;
        if(n % 2== 1){
            int r = n/2;
            for(int i=0;i<m/2;i++){
                int a = grid[r][i], b = grid[r][m-1-i];
                if(a == 0 && b == 0){
                    int p1 = min(t1, t0 + 2);
                    int p0 = min(t0, t1 + 2);
                    t0 = p0, t1 = p1;
                }
                else if(a + b == 1){
                    int tt = min(t0,t1) + 1;
                    t0 = t1 = tt;
                }
                else{
                    int p0 = min(t1, t0 + 2);
                    int p1 = min(t0, t1 + 2);
                    t0 = p0, t1 = p1;
                }
            }
        }

        if(m % 2== 1){
            int c = m/2;
            for(int i=0;i<n/2;i++){
                int a = grid[i][c], b = grid[n-1-i][c];
                if(a == 0 && b == 0){
                    int p1 = min(t1, t0 + 2);
                    int p0 = min(t0, t1 + 2);
                    t0 = p0, t1 = p1;
                }
                else if(a + b == 1){
                    int tt = min(t0,t1) + 1;
                    t0 = t1 = tt;
                }
                else{
                    int p0 = min(t1, t0 + 2);
                    int p1 = min(t0, t1 + 2);
                    t0 = p0, t1 = p1;
                }
            }
        }
        cout << t0 << " " << t1 << endl;

        if(n % 2 && m % 2) ans += grid[n/2][m/2];
        ans += t0;
        return ans;
    }
};
