class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:


        ans = 0
        h = len(grid)
        w = len(grid[0])

        ans4 = 0
        for i in range(h//2):
            for j in range(w//2):
                count = grid[i][j] + grid[i][-1-j] + grid[-1-i][-1-j] + grid[-1-i][j]
                ans4 += min(count, 4-count)

        ansYoko = 0
        oneCount = 0
        if h % 2 == 1:
            for j in range(w//2):
                if grid[h//2][j] != grid[h//2][-1-j]:
                    ansYoko +=1
                oneCount += grid[h//2][j] + grid[h//2][-1-j]

        ansTate = 0
        if w % 2 == 1:
            for i in range(h//2):
                if grid[i][w//2] != grid[-1-i][w//2]:
                    ansTate +=1
                oneCount += grid[i][w//2] + grid[-1-i][w//2]

        ansEx = 0
        if ansTate == 0 and ansYoko == 0:
            ansEx = oneCount % 4

        ansCenter = 0
        if h % 2 == 1 and w % 2 == 1:
            ansCenter = grid[h//2][w//2]

        print("ans4", ans4, "ansYoko", ansYoko, "ansTate", ansTate, "ansEx", ansEx, "ansCenter", ansCenter)

        return ans4 + ansYoko + ansTate + ansEx + ansCenter
