class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int flips=0;
        int ones=0;
        if(n%2==1)
        {
            for(int j=0;j<m/2;++j)
            {
                if(grid[n/2][j]==grid[n/2][m-j-1])
                {
                    ones+=grid[n/2][j]==1?2:0;
                }
                else 
                {
                    flips++;
                }
            }
        }
        if(m%2==1)
        {
            for(int i=0;i<n/2;++i)
            {
                if(grid[i][m/2]==grid[n-i-1][m/2])
                {
                    ones+=grid[i][m/2]==1?2:0;
                }
                else 
                {
                    flips++;
                }
            }
        }
        if(ones%4==2 && !flips)
        {
            flips+=2;
        }
        if(n%2==1 && m%2==1) if(grid[n/2][m/2]==1) flips++;
        for(int i=0;i<n/2;++i)
        {
            for(int j=0;j<m/2;++j)
            {
                int a=0,b=0;
                grid[i][j]==1?a++:b++;
                grid[i][m-j-1]==1?a++:b++;
                grid[n-i-1][j]==1?a++:b++;
                grid[n-i-1][m-j-1]==1?a++:b++;
                if(a==1 || a==3) flips++;
                else if(a==2) flips+=2;
            }
        }
            return flips;
    }
};
