class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans=0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int a=0, b=0;
                if(grid[i][j] == 0) a++;
                else b++;
                if(grid[i][m-j-1] == 0) a++;
                else b++;
                if(grid[n-i-1][j] == 0) a++;
                else b++;
                if(grid[n-i-1][m-j-1] == 0) a++;
                else b++;
                ans += min(a, b);
            }
        }
        int ne=0, e=0;
        if(m%2){
            int j = m/2;
            for(int i=0; i<n/2; i++){
                if(grid[i][j] != grid[n-i-1][j]) ne++;
                else{
                    if(grid[i][j] == 1) e++;
                }
            }
        }
        if(n%2){
            int i = n/2;
            for(int j=0; j<m/2; j++){
                if(grid[i][j] != grid[i][m-j-1]) ne++;
                else{
                    if(grid[i][j] == 1) e++;
                }
            }
        }
        if(e%2 == 0) ans += ne;
        else{
            if(ne > 0) ans += ne;
            else ans += 2;
        }
        if(n%2 && m%2){
            if(grid[n/2][m/2] == 1) ans++;
        }
        return ans;
    }
};
