class Solution {
    using pii = pair<int, int>;
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int res = 0;
        int c01 = 0;
        int c00 = 0;
        int c11 = 0;
        for(int i = 0; i <= m / 2; i++){
            for(int j = 0; j <= n / 2; j++){
                vector<int> cnt(2, 0);
                if(grid[i][j] == -1) continue;
                cnt[grid[i][j]]++;
                grid[i][j] = -1;
                set<pii> has;
                has.insert({i, j});

                if(n - 1 - j >= j && has.find({i, n-1-j}) == has.end()){
                    cnt[grid[i][n-1-j]]++;
                    grid[i][n-1-j]= -1;
                    has.insert({i, n-1-j});
                }

                if(n - 1 - j >= j && m-1-i >= i && has.find({m-1-i, n-1-j}) == has.end()){
                    cnt[grid[m-1-i][n-1-j]]++;
                    grid[m-1-i][n-1-j] = -1;
                    has.insert({m-1-i, n-1-j});
                }
                if( m-1-i >= i && has.find({m-1-i, j}) == has.end()){
                    cnt[grid[m-1-i][j]]++;
                    grid[m-1-i][j] = -1;
                    has.insert({m-1-i,j});
                }

                int incr = 0;
                if(has.size() == 4){
                    if(cnt[0] > cnt[1]) incr = cnt[1];
                    else {
                        incr = cnt[0];
                    }
                }
                else if (has.size() == 2){
                    if(cnt[0] == 2) c00++;
                    else if (cnt[1] == 2) c11++;
                    else c01++;
                }
                else if(has.size() == 1){
                    incr = cnt[1];
                }
                res += incr;
                //cout << cnt[0]  << " " <<  cnt[1] <<  " " << incr << endl;

            }
        }
        int ones = 2 * c11;
        res += c01;
        if(ones % 4 > 0 && c01 == 0){
            res += 2;
        }

        return res;
    }
};
