class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        int m = grid.size();
        int n = grid[0].size();
        for (int i = 0; i < m/2; i++) {
            for (int j = 0; j < n/2; j++) {
                int x = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j];
                res += min(x, 4-x);
            }
        }
        bool b = false;
        int c = 0;
        if (m % 2 == 1) {
            for (int j = 0; j < n/2; j++) {
                if (grid[m/2][j] != grid[m/2][n-1-j]) {
                    res++;
                    b = true;
                } else {
                    c += grid[m/2][j] + grid[m/2][n-1-j];
                }
            }
        }
        if (n % 2 == 1) {
            for (int i = 0; i < m/2; i++) {
                if (grid[i][n/2] != grid[m-1-i][n/2]) {
                    res++;
                    b = true;
                } else {
                    c += grid[i][n/2] + grid[m-1-i][n/2];
                }
            }
        }
        if (m % 2 == 1 && n % 2 == 1) res += grid[m/2][n/2];
        if (!b && (c % 4 != 0)) res += 2;
        return res;
    }
};
