class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int x = (n-2)/2;
        int y = (m-2)/2;
        if(m<=1) y= -1;
        if(n<=1) x= -1;
        int ans =0;
        for(int i=0;i<=x;i++){
            for(int j=0;j<=y;j++){
                int cnt0=0,cnt1=0;
                if(grid[i][j]==0) cnt0++;
                else cnt1++;
                if(grid[n-i-1][j]==0) cnt0++;
                else cnt1++;
                if(grid[i][m-j-1]==0) cnt0++;
                else cnt1++;
                if(grid[n-i-1][m-j-1]==0) cnt0++;
                else cnt1++;
                ans += min(cnt1,cnt0);
            }
        }
        cout<<ans<<endl;
        int cnt1=0;
        bool found = false;
        if(n&1){
            int p = n/2;
            for(int i=0;i<(m/2);i++){
                if(grid[p][m-i-1]!=grid[p][i]){
                    ans++;
                    found =true;
                } 
                else if(grid[p][i]==1) cnt1+=2;
            }
            if(m&1){
                if(grid[n/2][m/2]==1) ans++;
            }
        }
        if(m&1){
            cout<<"hellop";
            int p = m/2;
            for(int i=0;i<(n/2);i++){
                cout<<p<<" "<<i<<endl;
                if(grid[n-i-1][p]!=grid[i][p]){
                    ans++;
                    found =true;
                } 
                else if(grid[i][p]==1) cnt1+=2;
            }
        }
        cout<<cnt1<<" "<<found<<endl;
        if(cnt1%4!=0 && !found) ans+=2;
        return ans;
    }
};
