class Solution {
public:
    #define lli long long int
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size() , m = grid[0].size();

        int ans = 0;

        for(int i = 0 ; i < n/2 ; i++)
        {
            for(int j = 0 ; j < m/2 ; j++)
            {
                vector<int> cnt(2 , 0);
                cnt[grid[i][j]]++;
                cnt[grid[i][m-j-1]]++;
                cnt[grid[n-i-1][j]]++;
                cnt[grid[n-i-1][m-j-1]]++;
                ans+=min(cnt[0] , cnt[1]);
            }
        }

        lli tempCnt = 0;
        lli oneCnt = 0;
        if(n%2==1 && m%2==1)
        {
            ans+=grid[n/2][m/2];
            // // vector<int> cnt(2 , 0);
            // // cnt[grid[0][m/2]]++;
            // // cnt[grid[n/2][0]]++;
            // // cnt[grid[n/2][m-1]]++;
            // // cnt[grid[n-1][m/2]]++;
            // ans+=min(cnt[0] , cnt[1]);
        }
        if(n%2==1)
        {

            for(lli j = 0 ; j < m/2 ; j++)
            {
                if(grid[n/2][j]==grid[n/2][m-j-1])
                {
                    if(grid[n/2][j]==1)
                    {
                        oneCnt++;
                    }
                }
                else{
                    tempCnt++;
                    ans++;
                }
            }
        }
        if(m%2==1)
        {
            for(lli i = 0 ; i < n/2 ; i++)
            {
                if(grid[i][m/2]==grid[n-i-1][m/2])
                {
                    if(grid[i][m/2]==1)
                    {
                        oneCnt++;
                    }
                }
                else{
                    ans++;
                    tempCnt++;
                }
            }
        }
        if(tempCnt > 0) return ans;
        if(oneCnt&1)
        {
            return ans+2;
        }
        return ans;
    }
};
