class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int ans = 0;
        int equalOnes = 0;
        int unequal = 0;
        if(m & 1){
            int s = 0, e = n - 1;

            while(s < e){
                if(grid[m / 2][s] != grid[m / 2][e]){
                    ans++;
                    unequal++;
                }else if(grid[m / 2][s] == 1){
                    equalOnes += 2;
                }

                s++;
                e--;
            }
        }

        if(n & 1){
            int s = 0, e = m - 1;

            while(s < e){
                if(grid[s][n / 2] != grid[e][n / 2]){
                    ans++;
                    unequal++;
                }else if(grid[s][n / 2] == 1){
                    equalOnes += 2;
                }

                s++;
                e--;
            }
        }
        if(unequal == 0 && equalOnes % 4 == 2){
            ans += 2;
        }

        if((m & 1) && (n & 1)){
            if(grid[m / 2][n / 2] == 1){
                ans++;
            }
        }

        int sR = 0, eR = m - 1;
        while(sR < eR){
            int sC = 0, eC = n - 1;

            while(sC < eC){
                int curr = grid[sR][sC] + grid[sR][eC] + grid[eR][sC] + grid[eR][eC];
                if(curr == 1 || curr == 3){
                    ans++;
                }else if(curr == 2){
                    ans += 2;
                }

                sC++;
                eC--;
            }

            sR++;
            eR--;
        }

        return ans;
    }
};
