class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int dirx[]={i,i,-i,-i};
                int diry[]={j,-j,j,-j};
                int c=0;
                if(grid[i][j]==1) c++;
                if(grid[m-1-i][j]==1) c++;
                if(grid[m-1-i][n-1-j]) c++;
                if(grid[i][n-1-j]) c++;
                if(c==1) ans++;
                else if(c==2) ans+=2;
                else if(c==3) ans+=1; 
            }
        }
        int prev=0;
        bool a=false;
        cout<<ans<<endl;
        if(m%2){

            int k=m/2;
            for(int h=0;h<n/2;h++){
                if(grid[k][h]!=grid[k][n-1-h]) {
                    ans++;
                    a=true;
                }
                else{
                    if(grid[k][h]==1){
                        if(prev==0) prev+=2;
                        else prev=0;
                    }
                }
            }
        }
        cout<<ans<<endl;
        if(n%2){
            int k=n/2;
            for(int h=0;h<m/2;h++){
                if(grid[h][k]!=grid[m-1-h][k]){
                    ans++;
                    a=true;
                }
                else{
                    if(grid[h][k]==1){
                        if(prev==0) prev+=2;
                        else prev=0;
                    }
                }
            }
        }
        if(m%2 and n%2){
            ans+=grid[m/2][n/2];
        }
        if(a==false) ans+=prev;
        return ans;

    }
};
