class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n=v.size();
        int m=v[0].size();
        int starti=0;
        int endi=n-1;
        int tot=0;
        while(starti<endi)
        {
            int startj=0;
            int endj=m-1;
            while(startj<endj)
            {
                int tot0=v[starti][startj]+v[endi][startj]+v[starti][endj]+v[endi][endj];
                tot+=min(4-tot0,tot0);
                startj++;endj--;
            }
            starti++;endi--;
        }
        int op=0;
        int num=0;
        if(starti==endi)
        {
            int startj=0;int endj=m-1;
            while(startj<endj)
            {
                if(v[starti][startj]!=v[starti][endj])
                {
                        op++;
                    tot++;
                }   
                else if(v[starti][startj]==1)
                {
                    num++;   
                }
                startj++;endj--;
            }
        }
        if(m%2)
        {
            int starti=0;
            int endi=n-1;
            while(starti<endi)
            {
                if(v[starti][m/2]!=v[endi][m/2])
                {
                        op++;
                    tot++;
                }   
                else if(v[starti][m/2]==1)
                {
                    num++;   
                }
                starti++;endi--;
            }
        }
        if(n%2 && m%2 && v[n/2][m/2]==1){tot++;}
        if(num%2 && op==0)
        {
            tot+=2;
        }
        return tot;



    }
};
