class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        row_mid_l = len(grid) // 2
        row_mid_r = (len(grid)+1) // 2
        col_mid_l = len(grid[0]) // 2
        col_mid_r = (len(grid[0])+1) // 2

        u_l = [
            grid[r][c]
            for c in range(col_mid_l)
            for r in range(row_mid_l)
        ]
        u_r = [
            grid[r][c]
            for c in range(len(grid[0])-1, col_mid_r-1, -1)
            for r in range(row_mid_l)
        ]
        l_l = [
            grid[r][c]
            for c in range(col_mid_l)
            for r in range(len(grid)-1, row_mid_r-1, -1)
        ]
        l_r = [
            grid[r][c]
            for c in range(len(grid[0])-1, col_mid_r-1, -1)
            for r in range(len(grid)-1, row_mid_r-1, -1)
        ]

        cell_flips = 0
        for cells in zip(u_l, u_r, l_l, l_r):
            ones = sum(cells)
            zeros = 4-ones
            cell_flips += min([ones, zeros])

        row_flips = 0
        row_ones = 0
        if len(grid) % 2 == 1:
            l = grid[row_mid_l][:col_mid_l]
            r = grid[row_mid_l][len(grid[0])-1:col_mid_r-1:-1]
            row_ones = sum(l+r)
            row_flips = sum([abs(a-b) for a,b in zip(l,r)])

        col_flips = 0
        col_ones = 0
        if len(grid[0]) % 2 == 1:
            l = [
                grid[row_idx][col_mid_l]
                for row_idx in range(row_mid_l)
            ]
            r = [
                grid[row_idx][col_mid_l]
                for row_idx in range(len(grid)-1, row_mid_r-1, -1)
            ]
            col_ones = sum(l+r)
            col_flips = sum([abs(a-b) for a,b in zip(l,r)])

        center_flips = 0
        if len(grid[0]) % 2 == 1 and len(grid) % 2 == 1:
            center_flips = grid[row_mid_l][col_mid_l]

        total_flips = cell_flips + row_flips + col_flips + center_flips
        if (row_ones + col_ones) % 4 == 2:
            if row_flips == 0 and col_flips == 0:
                total_flips += 2
        return total_flips
