class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size(),countOne=0,ans=0,ans1=0;

        for(int row=0;row<m/2;row++) {
            for(int col=0;col<n/2;col++) {
                countOne=0;
                countOne=countOne+(grid[row][col]==1?1:0);
                countOne=countOne+(grid[row][n-1-col]==1?1:0);
                countOne=countOne+(grid[m-1-row][col]==1?1:0);
                countOne=countOne+(grid[m-1-row][n-1-col]==1?1:0);
                ans+=min(countOne,4-countOne);
            }
        }

        countOne=0;
        if(m%2==1) {
            for(int col=0;col<n/2;col++) {
                if(grid[m/2][col]!=grid[m/2][n-1-col])
                    ans1++;
                if(grid[m/2][col]==grid[m/2][n-1-col] && grid[m/2][col]==1)
                    countOne+=2;
            }
        }

        if(n%2==1) {
            for(int row=0;row<m/2;row++) {
                if(grid[row][n/2]!=grid[m-1-row][n/2])
                    ans1++;
                if(grid[row][n/2]==grid[m-1-row][n/2] && grid[row][n/2]==1)
                    countOne+=2;
            }
        }

        if(countOne%4!=0 && ans1==0)
            ans1+=2;

        if(m%2==1 && n%2==1 && grid[m/2][n/2]==1)
            ans++;
        return ans+ans1;
    }
};
