class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m=len(grid)
        n=len(grid[0])
        m2=m//2
        n2=n//2

        ans=0
        for i in range(m2):
            for j in range(n2):
                a=grid[i][j]
                b=grid[m-1-i][j]
                c=grid[i][n-1-j]
                d=grid[m-1-i][n-1-j]
                e = a+b+c+d
                e=e%4
                if e==2:
                    ans+=2
                elif e==1 or e==3:
                    ans+=1

        a1=0
        b1=0
        if n%2==1:
            j=n2
            for i in range(m2):
                a = grid[i][j]
                b = grid[m - 1 - i][j]
                if a!=b:
                    b1+=1
                elif a==1:
                    a1+=1

        if m % 2 == 1:
            l = grid[m2]
            for i in range(n2):
                a = l[i]
                b = l[n - 1 - i]
                if a != b:
                    b1 += 1
                elif a == 1:
                    a1 += 1

        a1 = a1 % 2
        if a1 == 1 and b1 == 0:
            b1 += 2
        ans += b1

        if n%2==1 and m % 2 == 1:
            if grid[m2][n2]==1:
                ans+=1


        return ans
