class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        int a[2] = {0, 0};
        int op = 0;
        for(int i = 0; i <= (n - 1) / 2; i ++) {
            for(int j = 0; j <= (m - 1) / 2; j ++) {
                bool flag1 = n % 2 == 0 || i != (n - 1) / 2;
                bool flag2 = m % 2 == 0 || j != (m - 1) / 2;
                int cnt[2] = {0, 0};
                int tot = 0;
                if(flag1 && flag2) {
                    cnt[grid[i][j]] ++;
                    cnt[grid[n - 1 - i][j]] ++;
                    cnt[grid[i][m - 1 - j]] ++;
                    cnt[grid[n - 1 - i][m - 1 - j]] ++;
                    tot = 4;
                } else if(flag1) {
                    cnt[grid[i][j]] ++;
                    cnt[grid[n - 1 - i][j]] ++;
                    if(grid[i][j] != grid[n - 1 - i][j]) op ++;
                    else a[grid[i][j]] += 2;
                    tot = 2;
                } else if(flag2) {
                    cnt[grid[i][j]] ++;
                    cnt[grid[i][m - 1 - j]] ++;
                    if(grid[i][j] != grid[i][m - 1 - j]) op ++;
                    else a[grid[i][m - 1 - j]] += 2;
                    tot = 2;
                } else {
                    a[grid[i][j]] ++;
                    cnt[grid[i][j]] ++;
                    tot = 1;
                }
                ans += tot - max(cnt[0], cnt[1]);
            }
        }


        if(a[1] % 4 == 0) return ans;
        else if(a[1] % 4 == 1) {
            return ++ ans;
        } else if(a[1] % 4 == 2) {
            return op ? ans : ans + 2;
        } else {
            ans ++;
            return op ? ans : ans + 2;
        }
    }
};
