class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        // cout<<n/2<<" "<<m/2<<endl;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int onecnt=0,zerocnt=0;
                if(grid[i][j]==0)zerocnt++;
                else onecnt++;
                if(grid[n-i-1][j]==0)zerocnt++;
                else onecnt++;
                if(grid[i][m-j-1]==0)zerocnt++;
                else onecnt++;
                if(grid[n-i-1][m-j-1]==0)zerocnt++;
                else onecnt++;
                ans+=min(zerocnt,onecnt);
            }
        }
        int cnt=0,one=0,zero=0;
        if(n%2==1){
            int i=n/2;
            for(int j=0;j<m/2;j++){
                if(grid[i][j]!=grid[i][m-j-1])
                cnt++;
                else if(grid[i][j]==1)
                one+=2;
                else
                zero+=2;
            }
        }
        if(m%2==1){
            int j=m/2;
            for(int i=0;i<n/2;i++){
                if(grid[i][j]!=grid[n-i-1][j])
                cnt++;
                else if(grid[i][j]==1)
                one+=2;
                else
                zero+=2;
            }
        } 
        // cout<<one<<" "<<zero;
        if(one%4!=0 && cnt==0)
        cnt+=2;
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1)
        cnt++;
        ans+=cnt;
        return ans;
    }
};
