class Solution {
public:
     void Count(vector<vector<int>>& grid,int i, int j,int &count0,int &count1){
          int n =grid.size();
          int m = grid[0].size();

         if(grid[i][j]==0){
             count0++;
         }else{
             count1++;
         }
          if(j!=m-1-j&&grid[i][m-1-j]==0){
             count0++;
         }else if(j!=m-1-j){
             count1++;
         }
         if(i!=n-1-i&&grid[n-1-i][j]==0){
             count0++;
         }else if(i!=n-1-i){
             count1++;
         }
         if(j!=m-1-j&&i!=n-1-i&&grid[n-1-i][m-1-j]==0){
             count0++;
         }else if(j!=m-1-j&&i!=n-1-i){
             count1++;
         }
     }

     int MatPalindromic(vector<vector<int>>& grid) {
        int n =grid.size();
        int m = grid[0].size();
        int ans =0;
        for(int i =0;i<(n+1)/2;i++){
            for(int j =0;j<(m+1)/2;j++){
                int count0 = 0;
                int count1 = 0;
                Count(grid,i,j,count0,count1);
                ans+=(min(count0,count1));
             }
        }
        if(n%2==1&&m%2==1&&grid[n/2][m/2]) ans++;
        int count_Match =0;
        int count_not_match =0;
        if(n%2==1){
            int l =0;
            int r = m-1;
            while(l<r){
                if(grid[n/2][l]!=grid[n/2][r]){
                    count_not_match++;
                }else if(grid[n/2][l]){
                    count_Match++;
                }
                l++;
                r--;
            }
        }
         if(m%2==1){
            int l =0;
            int r = n-1;
            while(l<r){
                if(grid[l][m/2]!=grid[r][m/2]){
                    count_not_match++;
                }else if(grid[l][m/2]){
                    count_Match++;
                }
                l++;
                r--;
            }
        }
         if(count_Match%2==1&&count_not_match==0){
             ans+=2;
        }
        return ans;
    }


    int minFlips(vector<vector<int>>& grid) {
        return MatPalindromic(grid);
    }
};
