class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        const int R = (int) grid.size();
        const int C = (int) grid[0].size();

        int ans = 0;
        int have_ones = 0;
        for(int r = 0; r < R / 2; ++r){
            for(int c = 0; c < C / 2; ++c){
                int cnt = 0;
                cnt += grid[r][c];
                cnt += grid[r][C - 1 - c];
                cnt += grid[R - 1 - r][c];
                cnt += grid[R - 1 - r][C - 1 - c];
                ans += min(cnt, 4 - cnt);
                if(cnt >= 2) have_ones += 4;
            }
        }

        if((R % 2 == 0) && (C % 2) == 0){
            return ans;
        }else{
            if((R % 2 == 1) && (C % 2) == 1){
                if(grid[R / 2][C / 2] == 1){
                    ans++;
                }                
            }

            int zer = 0;
            int one = 0;
            int two = 0;
            if(C % 2 == 1){
                for(int r = 0; r < R / 2; ++r){
                    if(grid[r][C / 2] == 1 && grid[R - 1 - r][C / 2] == 1){
                        two++;
                    }else if(grid[r][C / 2] == 0 && grid[R - 1 - r][C / 2] == 0){
                        zer++;
                    }else{
                        one++;
                    }
                }
            }

            if(R % 2 == 1){
                for(int c = 0; c < C / 2; ++c){
                    if(grid[R / 2][c] == 1 && grid[R / 2][C - 1 - c] == 1){
                        two++;
                    }else if(grid[R / 2][c] == 0 && grid[R / 2][C - 1 - c] == 0){
                        zer++;
                    }else{
                        one++;
                    }
                }
            }
            have_ones += two * 2;
            if(have_ones % 4 == 0){
                ans += one;
            }else{
                if(one >= 1){
                    ans += one;
                }else{
                    ans += 2;
                }
            }
        }
        return ans;
    }
};
