class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int moves=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int c=grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];
                grid[i][j]=1;
                grid[n-1-i][j]=1;
                grid[i][m-1-j]=1;
                grid[n-1-i][m-1-j]=1;
                moves+=min(c,4-c);
            }
        }
        int ones=0;
        int k=0;
        if(n&1)
        {
            for(int i=0;i<m/2;i++)
            {
                if(grid[n/2][i]!=grid[n/2][m-1-i])
                {
                    grid[n/2][i]=1;
                    grid[n/2][m-1-i]=1;
                    k++;
                    ones+=2;
                }
            }
        }
        if(m&1)
        {
            for(int i=0;i<n/2;i++)
            {
                if(grid[i][m/2]!=grid[n-1-i][m/2])
                {
                    grid[i][m/2]=1;
                    grid[n-1-i][m/2]=1;
                    k++;
                    ones+=2;
                }
            }
        }
        if((n&1)&&(m&1))
        {
            if(grid[n/2][m/2])
            {
                moves++;
                grid[n/2][m/2]=0;
            }
        }
        for(auto &i:grid)
        {
            for(auto &j:i)
            {
                ones+=j==1;
            }
        }


        if(ones%4)
        {
            if(!k)
            {
              moves+=2;   
            }
        }
        return moves+k;

    }
};
