class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        if (m == 1 && n == 1)
        {
            return grid[0][0] == 1;
        }

        int res = 0;
        for (int i = 0; i < m / 2; i++)
        {
            for (int j = 0; j < n / 2; j++)
            {
                int sum = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 - j]
                + grid[m - 1 - i][n - 1 - j];
                res += min(sum, 4 - sum);
            }
        }

        if ((m % 2) && (n % 2) && grid[m / 2][n / 2])
        {
            res++;
        }

        int diff = 0, one = 0;
        if (m % 2)
        {
            for (int j = 0; j < n / 2; j++)
            {
                diff += grid[m / 2][j] != grid[m / 2][n - 1 - j];
                one += grid[m / 2][j] == 1 && grid[m / 2][j] == grid[m / 2][n - 1 - j];
            }
        }

        if (n % 2)
        {
            for (int i = 0; i < m / 2; i++)
            {
                diff += grid[i][n / 2] != grid[m - 1 - i][n / 2];
                one += grid[i][n / 2] == 1 && grid[i][n / 2] == grid[m - 1 - i][n / 2];
            }
        }

        res += diff;
        if ((one % 2) && diff == 0)
        {
            res += 2;
        }

        return res;
    }
};
