class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int ans = 0;
        for(int i = 0; i < m/2; i++)
            for(int j = 0; j < n/2; j++){
                int numOfZero = 0;
                if(grid[i][j] == 0) numOfZero++;
                if(grid[m-1-i][j] == 0) numOfZero++;
                if(grid[i][n-1-j] == 0) numOfZero++;
                if(grid[m-1-i][n-1-j] == 0) numOfZero++;
                //System.out.println(numOfZero);
                if(numOfZero == 1 || numOfZero == 3) ans += 1;
                else if(numOfZero == 2) ans += 2;
            }
        if(m % 2 == 0 && n % 2 == 0) return ans;
        if(m % 2 == 1 && n % 2 == 0){
            int numOfOne = 0;
            int times = 0;
            for(int j = 0; j < n/2; j++){
                if(grid[m/2][j] != grid[m/2][n-1-j]) times++;
                if(grid[m/2][j] == 1) numOfOne++;
            }
            if(times == 0 && numOfOne % 2 == 1) ans += 2;
            else if(times != 0) ans += times;
            return ans;
        }
        if(m % 2 == 0 && n % 2 == 1){
            int numOfOne = 0;
            int times = 0;
            for(int i = 0; i < m/2; i++){
                if(grid[i][n/2] != grid[m-1-i][n/2]) times++;
                if(grid[i][n/2] == 1) numOfOne++;
            }
            if(times == 0 && numOfOne % 2 == 1) ans += 2;
            else if(times != 0) ans += times;
            return ans;
        }
        int numOfOne = 0;
        int timesRow = 0, timesCol = 0;
        for(int i = 0; i < m/2; i++){
            if(grid[i][n/2] != grid[m-1-i][n/2]) timesCol++;
            if(grid[i][n/2] == 1) numOfOne++;
        }
        for(int j = 0; j < n/2; j++){
            if(grid[m/2][j] != grid[m/2][n-1-j]) timesRow++;
            if(grid[m/2][j] == 1) numOfOne++;
        }
        if(timesCol == 0 && timesRow == 0 && numOfOne % 2 == 1) ans += 2;
        else ans += timesRow + timesCol;
        if(grid[m/2][n/2] == 1) ans += 1;
        return ans;
    }
}
